import React, { useRef } from 'react';
import Sketch from 'react-p5';
import MersenneTwister from 'mersenne-twister';
import Mappa from "mappa.js";
import 'p5.easycam.js';
//-------------------------------------------
let DEFAULT_SIZE = 500;
//-------------------------------------------
let frameCountPerCicle = 200;
let CFrameCount,CQuartEaseInR,CQuartEaseOutR;
let CProgressR,CQuadEaseInR,CQuadEaseOutR;
let back,back2;
//-------------------------------------------
const CustomStyle = ({
  block,
  canvasRef,
  attributesRef,
  width,
  height,
  handleResize,
  mod1 = 0.75, // Example: replace any number in the code with mod1, mod2, or color values
  mod2 = 0.25,
  color1 = 'hsl(170,90%,10%)',
  background = 'hsl(110,80%,10%)',
}) => {
  const shuffleBag = useRef();
  const hoistedValue = useRef();
  const { hash } = block;
  //-------------------------------------------
  const setup = (p5, canvasParentRef) => {
    // Keep reference of canvas element for snapshots
    let _p5 = p5.createCanvas(width, height).parent(canvasParentRef);
    canvasRef.current = p5;

    attributesRef.current = () => {
      return {
        attributes: [
          {
            display_type: 'number',
            trait_type: 'your trait here number',
            value: hoistedValue.current,
          },
          {
            trait_type: 'your trait here text',
            value: 'replace me',
          },
        ],
      };
    };
  };

  const draw = (p5) => {
    let WIDTH = width;
    let HEIGHT = height;
    let DIM = Math.min(WIDTH, HEIGHT);
    let M = DIM / DEFAULT_SIZE;
    updateCProgress(p5);
    //-------------------------------------------mainShape
    p5.background(background);
    p5.blendMode(p5.BLEND);
    p5.noStroke();
    p5.textSize(0.10);
    p5.perspective(60 * p5.PI/180, width/height, 0.1, 5000);
    back=p5.abs(((p5.millis()%6000) / 6000)-0.5)-0.5;
    back2=p5.millis() / 3000;
    //------------------------------------------- reset shuffle bag
    {
      let seed = parseInt(hash.slice(0, 16), 16);
      shuffleBag.current = new MersenneTwister(seed);
      let objs = block.transactions.map((t) => {
        let seed = parseInt(t.hash.slice(0, 16), 16);
        return {
          y: shuffleBag.current.random(),
          x: shuffleBag.current.random(),
          radius: seed / 1000000000000000,
        };
      });

      //
      hoistedValue.current = 42;

      objs.map((dot, i) => {
        p5.stroke(color1);
        p5.strokeWeight(1 + mod2 * 10);
        p5.ellipse(
          200 * dot.y * 6 * M,
          100 * dot.x * 6 * M,
          dot.radius * M * mod1
        );
      });
    }
    //-------------------------------------------
  };

  return <Sketch setup={setup} draw={draw} windowResized={handleResize} />;
};

export default CustomStyle;

const styleMetadata = {
  name: '',
  description: '',
  image: '',
  creator_name: '',
  options: {
    mod1: 0.15,
    mod2: 0.7,
    color1 : 'hsl(170,90%,30%)',
    background : 'hsl(110,80%,10%)'
  },
};

function updateCProgress(p5) {
  CFrameCount = p5.frameCount % frameCountPerCicle;
  CProgressR = CFrameCount / frameCountPerCicle;
  CQuadEaseInR = CProgressR * CProgressR;
  CQuadEaseOutR = -p5.sq(CProgressR - 1) + 1;
  CQuartEaseInR = p5.pow(CProgressR, 4);
  CQuartEaseOutR = -p5.pow(CProgressR - 1, 4) + 1;
}

export { styleMetadata };
